model.expression:
(is_not_null(to_timestamp(col("eff_dt"), "yyyyMMdd"))
    .and(
      is_not_null(to_timestamp(col("term_dt"), "yyyyMMdd")).and(
        (isnull(col("run_eff_dt"))
          .and(col("term_dt") >= lit(Config.BUSINESS_DATE)))
          .or(
            is_not_null(col("run_eff_dt")).and(
              col("term_dt") >= concat(
                string_substring(
                  date_format(
                    to_date(
                      date_add_months(date_format(to_date(lit(Config.BUSINESS_DATE), "yyyyMMdd"), "yyyy-MM-dd"), 1),
                      "yyyy-MM-dd"
                    ),
                    "yyyyMMdd"
                  ),
                  lit(1),
                  lit(6)
                ),
                lit("01")
              )
            )
          )
      )
    ))
    .and(isnull(col("inactive_dt")).or(is_not_null(to_timestamp(col("inactive_dt"), "yyyyMMdd"))))

!(is_not_null(to_timestamp(col("eff_dt"), "yyyyMMdd"))
    .and(
      is_not_null(to_timestamp(col("term_dt"), "yyyyMMdd")).and(
        (isnull(col("run_eff_dt"))
          .and(col("term_dt") >= lit(Config.BUSINESS_DATE)))
          .or(
            is_not_null(col("run_eff_dt")).and(
              col("term_dt") >= concat(
                string_substring(
                  date_format(
                    to_date(
                      date_add_months(date_format(to_date(lit(Config.BUSINESS_DATE), "yyyyMMdd"), "yyyy-MM-dd"), 1),
                      "yyyy-MM-dd"
                    ),
                    "yyyyMMdd"
                  ),
                  lit(1),
                  lit(6)
                ),
                lit("01")
              )
            )
          )
      )
    ))
    .and(isnull(col("inactive_dt")).or(is_not_null(to_timestamp(col("inactive_dt"), "yyyyMMdd"))))

expression.expression:
coalesce(
    trim(col("inactive_dt")),
    when(
      col("inactive_dt") === lit("0"),
      first_defined(
        adjustCenturyDateInCyyFormat(lpad(col("term_dt"), 7, "0").cast(StringType), lit("1900"), lit("CyyMMdd")),
        lit("INVALID")
      )
    ).otherwise(
      first_defined(
        adjustCenturyDateInCyyFormat(lpad(col("inactive_dt"), 7, "0").cast(StringType), lit("1900"), lit("CyyMMdd")),
        lit("INVALID")
      )
    )
  )
