import _root_.io.prophecy.abinitio.ScalaFunctions._
import scala.collection.mutable.ArrayBuffer

val process_udf = udf({ (row: Row) =>
  val results = ArrayBuffer[Row]()
  var _l1_factor = BigDecimal(5)
  var _l1_denominator = BigDecimal(100)
  var _l1_assocition_cnt = BigDecimal(2000)
  var _l2_factor = BigDecimal(1000)
  var tal_dtl_final = Array[Row]()
  var i = 0
  var j = 0
  var nested_tal_l1_dtl = row.getAs[Seq[Row]](11).toArray
  while (compareTo(i, nested_tal_l1_dtl.length) < 0) {
    if (!_isnull(nested_tal_l1_dtl(convertToInt(i)).getAs[String]("tal_assoc_name"))) {
          val nested_tal_l1_dtl_i = Row(
            nested_tal_l1_dtl(convertToInt(i)).getAs[java.math.BigDecimal]("tal_dtl_id"),
            nested_tal_l1_dtl(convertToInt(i)).getAs[java.math.BigDecimal]("tal_id"),
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("tal_name"),
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("tal_desc"),
            (nested_tal_l1_dtl(convertToInt(i)).getAs[String]("tal_dtl_type_cd")),
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("nested_tal_name"),
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("tal_assoc_name"),
            (BigDecimal(row.getAs[String]("priority")) + (BigDecimal(
              nested_tal_l1_dtl(convertToInt(i)).getAs[String]("priority")
            ) / (_l1_denominator * (_l1_factor / _l1_assocition_cnt)))).toString,
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("eff_dt"),
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("term_dt"),
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("newline")
          )
          tal_dtl_final = Array.concat(tal_dtl_final, Array.fill(1)(nested_tal_l1_dtl_i))
    } else {
      j = 0
      val nested_tal_l1_dtl_arr = nested_tal_l1_dtl(i).getAs[Seq[Row]](0).toArray
      while (
        compareTo(j,
              nested_tal_l1_dtl_arr.length    
        ) < 0
      ) {
        var nested_tal_l2_dtl = nested_tal_l1_dtl_arr(convertToInt(j))
        nested_tal_l2_dtl = updateIndexInRow(
          nested_tal_l2_dtl,
          7,
          ((BigDecimal(row.getAs[String]("priority")) + (BigDecimal(
            nested_tal_l1_dtl(convertToInt(i)).getAs[String]("priority")
          ) / (_l1_denominator * (_l1_factor / _l1_assocition_cnt)))) + (BigDecimal(
            nested_tal_l2_dtl.getAs[String]("priority")
          ) / (_l2_factor * _l2_factor))).toString
        )
        tal_dtl_final = Array.concat(tal_dtl_final, Array.fill(1)(nested_tal_l2_dtl))
        j = j + 1
      }
    }
    i = i + 1
  }

  tal_dtl_final.zipWithIndex.foreach {
    case (r, idx) ⇒
      results.append(
        Row(
        row.getAs[java.math.BigDecimal](0),
        row.getAs[java.math.BigDecimal](1),
        row.getAs[String](2),
        row.getAs[String](3),
        row.getAs[String](4),
        tal_dtl_final(idx).getAs[String](2),
        if (tal_dtl_final(idx).getAs[String](6) != null)  tal_dtl_final(idx).getAs[String](6) else row.getAs[String](6),
        if (tal_dtl_final(idx).getAs[String](7) != null)  tal_dtl_final(idx).getAs[String](7) else row.getAs[String](7),
        row.getAs[String](8),
        row.getAs[String](9),
        row.getAs[String](10),
      ))
  }
  results.toArray
}, ArrayType(StructType(
    List(
      StructField("tal_dtl_id",      DecimalType(10, 0),  false),
      StructField("tal_id",          DecimalType(10, 0),  false),
      StructField("tal_name",        StringType,         false),
      StructField("tal_desc",        StringType,         false),
      StructField("tal_dtl_type_cd", StringType,         false),
      StructField("nested_tal_name", StringType,         true),
      StructField("tal_assoc_name",  StringType,         true),
      StructField("priority",        StringType,         false),
      StructField("eff_dt",          StringType,         false),
      StructField("term_dt",         StringType,         false),
      StructField("newline",         StringType,         true)
    )
)))

def nested_tal_l1_dtl() = {
  lookup_row("TAL_Container_Dtls", col("nested_tal_name"))
}

def nested_tal_l2_dtl() = {
    transform(
      nested_tal_l1_dtl(),
      xx =>
        lookup_row("TAL_Container_Dtls", xx.getField("nested_tal_name"))
    )
}

val df = in
  .withColumn(
    "nested_tal_l1_dtl",
    nested_tal_l1_dtl(),
  ).withColumn(
    "nested_tal_l2_dtl",
    nested_tal_l2_dtl()
  )

val origColumns = df.columns.map(col)
val out = df
  .select(struct(origColumns: _*).as("inputRows"))
  .select(explode(process_udf(col("inputRows"))).alias("output"))
  .select(col("output.*"))

import _root_.io.prophecy.abinitio.ScalaFunctions._

val process_udf = udf(
{ (input: Seq[Row]) ⇒
    val outputRows = scala.collection.mutable.ArrayBuffer[Row]()

    var excl_target_prdcts = Array[Byte]()
    var excl_alt_prdcts    = Array[Byte]()
    var target_prdcts      = Array[Row]()
    var alt_prdcts         = Array[Row]()

    input.foreach { in ⇒
    var lv_target_prdcts = Array[Byte]()
    var lv_alt_prdcts    = Array[Byte]()
    if (in.getAs[String]("tal_assoc_type_cd").toInt == 3) {
        excl_target_prdcts =
        _bv_or(excl_target_prdcts, _bv_vector_or(in.getSeq[Row](8).toArray.map(r ⇒ r.getAs[Array[Byte]](2))))
        excl_alt_prdcts =
        _bv_or(excl_alt_prdcts, _bv_vector_or(in.getSeq[Row](9).toArray.map(r ⇒ r.getAs[Array[Byte]](2))))
    } else {
        in.getSeq[Row](8).toArray.foreach { target_prds ⇒
            lv_target_prdcts = _bv_difference(target_prds.getAs[Array[Byte]](2), excl_target_prdcts)
            if (_bv_count_one_bits(lv_target_prdcts) > 0) {
                target_prdcts = Array
                .concat(
                    target_prdcts,
                    Array.fill(1)(
                    Row(target_prds.getAs[String](0), target_prds.getAs[String](1), lv_target_prdcts)
                    )
                )
            }
        }

        in.getSeq[Row](9).toArray.foreach { alt_prds ⇒
            lv_alt_prdcts = _bv_difference(alt_prds.getAs[Array[Byte]](2), excl_alt_prdcts)
            if (_bv_count_one_bits(lv_target_prdcts) > 0) {
                alt_prdcts = Array
                .concat(
                    alt_prdcts,
                    Array.fill(1)(
                    Row(alt_prds.getAs[String](0),
                        alt_prds.getAs[String](1),
                        lv_alt_prdcts,
                        alt_prds.getAs[String](3),
                        alt_prds.getAs[String](4),
                        alt_prds.getAs[String](5)
                    )
                    )
                )
            }
        }
    }
    outputRows.append(
        Row(
        in.getAs[java.math.BigDecimal](0),
        in.getAs[String](1),
        in.getAs[String](2),
        in.getAs[String](3),
        in.getAs[String](4),
        in.getAs[String](5),
        in.getAs[String](6),
        in.getAs[String](7),
        target_prdcts,
        alt_prdcts,
        in.getAs[String](10),
        in.getAs[String](11),
        in.getAs[String](12),
        in.getAs[String](13)
        )
    )
    }
    outputRows.toArray
},
ArrayType(
    StructType(
    List(
        StructField("tal_id",             StringType, true),
        StructField("tal_name",           StringType, true),
        StructField("tal_assoc_name",     StringType, true),
        StructField("clinical_indn_desc", StringType, true),
        StructField("tal_desc",           StringType, true),
        StructField("tal_assoc_desc",     StringType, true),
        StructField("priority",           StringType, true),
        StructField("tal_assoc_type_cd",  StringType, true),
        StructField(
        "target_prdcts",
        ArrayType(StructType(
                    List(StructField("udl_nm",   StringType, true),
                            StructField("udl_desc", StringType, true),
                            StructField("products", BinaryType, true)
                    )
                    ),
                    true
        ),
        true
        ),
        StructField(
        "alt_prdcts",
        ArrayType(
            StructType(
            List(
                StructField("udl_nm",            StringType, true),
                StructField("udl_desc",          StringType, true),
                StructField("products",          BinaryType, true),
                StructField("constituent_group", StringType, true),
                StructField("constituent_reqd",  StringType, true),
                StructField("constituent_rank",  StringType, true)
            )
            ),
            true
        ),
        true
        ),
        StructField("shared_qual",       StringType, true),
        StructField("override_tac_name", StringType, true),
        StructField("override_tar_name", StringType, true),
        StructField("newline",           StringType, true)
    )
    )
)
)

val origColumns = in0.columns.map(col)
val out0 = in0
.groupBy("tal_id")
.agg(
    collect_list(
    struct(
        origColumns: _*
    )
    ).alias("inputRows")
)
.select(explode(process_udf(col("inputRows"))).alias("output"))
.select(col("output.*"))

import _root_.io.prophecy.abinitio.ScalaFunctions._
val process_udf = udf(
    { (inputRows: Seq[Row], clinical_indn_desc: Array[String]) ⇒
    var target_prdcts_info  = Array[Row]()
    var all_target_prdcts   = Array[Byte]()
    var all_alt_prdcts      = Array[Byte]()
    var alt_prdcts_info     = Array[Row]()
    var target_udl_name     = Array[String]()
    var alternate_udl_name  = Array[String]()
    var clinc_indi_info     = Array[Row]()
    var _tal_assoc_name     = Array[String]()
    var _clinical_indn_desc = Array[String]()

    inputRows.foreach { row ⇒
        if (
        !_isnull(row.getAs[String]("target_udl_name")) && !target_udl_name
            .contains(row.getAs[String]("target_udl_name"))
        ) {
        var trgt_prdcts_lkp = row.getAs[Row]("trgt_prdcts_lkp")
        if (!_isnull(trgt_prdcts_lkp.getAs[String]("udl_nm"))) {
            if (_bv_count_one_bits(trgt_prdcts_lkp.getAs[Array[Byte]]("products")) > 0) {
            target_prdcts_info = Array.concat(target_prdcts_info, Array.fill(1)(trgt_prdcts_lkp))
            }
        }
        target_udl_name = Array.concat(target_udl_name, Array.fill(1)(row.getAs[String]("target_udl_name")))
        }

        if (
        !_isnull(row.getAs[String]("alt_udl_name")) && !alternate_udl_name
            .contains(row.getAs[String]("alt_udl_name"))
        ) {
        var alt_prdcts_lkp = row.getAs[Row]("alt_prdcts_lkp")
        if (!_isnull(alt_prdcts_lkp.getAs[String]("udl_nm"))) {
            if (_bv_count_one_bits(alt_prdcts_lkp.getAs[Array[Byte]]("products")) > 0) {
            alt_prdcts_info = Array.concat(alt_prdcts_info, Array.fill(1)(alt_prdcts_lkp))
            }
        }
        alternate_udl_name = Array.concat(alternate_udl_name, Array.fill(1)(row.getAs[String]("alt_udl_name")))
        }
    }

    var lastRow = inputRows.toArray.last

    if (!_tal_assoc_name.contains(lastRow.getAs[String]("tal_assoc_name"))) {
        _clinical_indn_desc = clinical_indn_desc
        clinc_indi_info =
        Array.concat(clinc_indi_info,
                        Array.fill(1)(Row(lastRow.getAs[String]("tal_assoc_name"), _clinical_indn_desc))
        )
    } else {
        var idx = _tal_assoc_name.indexOf(lastRow.getAs[String]("tal_assoc_name"))
        if (idx < _clinical_indn_desc.length)
        _clinical_indn_desc = clinc_indi_info(idx).getAs[Array[String]](1)
    }
    if (lastRow.getAs[String]("tal_assoc_type_cd").toInt == 3) {
        target_prdcts_info =
        if (target_prdcts_info.isEmpty && alt_prdcts_info.nonEmpty) Array(Row("", "", _bv_all_zeros()))
        else target_prdcts_info
        alt_prdcts_info =
        if (target_prdcts_info.nonEmpty && alt_prdcts_info.isEmpty) Array(Row("", "", _bv_all_zeros(), "", "", ""))
        else alt_prdcts_info
    }

    Row(
        lastRow.getAs[java.math.BigDecimal]("tal_assoc_dtl_id"),
        lastRow.getAs[java.math.BigDecimal]("tal_assoc_id"),
        lastRow.getAs[String]("tal_assoc_name"),
        _clinical_indn_desc.mkString("\\|"),
        lastRow.getAs[String]("tal_assoc_desc"),
        lastRow.getAs[String]("tal_assoc_type_cd"),
        target_prdcts_info,
        alt_prdcts_info,
        lastRow.getAs[String]("shared_qual"),
        lastRow.getAs[String]("override_tac_name"),
        lastRow.getAs[String]("override_tar_name"),
        lastRow.getAs[String]("newline")
    )
    },
    StructType(
    List(
        StructField("tal_assoc_dtl_id",   DecimalType(10, 0), true),
        StructField("tal_assoc_id",       DecimalType(10, 0), true),
        StructField("tal_assoc_name",     StringType,         true),
        StructField("clinical_indn_desc", StringType,         true),
        StructField("tal_assoc_desc",     StringType,         true),
        StructField("tal_assoc_type_cd",  StringType,         true),
        StructField(
        "target_udl_info",
        ArrayType(StructType(
                    List(StructField("udl_nm",   StringType, true),
                            StructField("udl_desc", StringType, true),
                            StructField("products", BinaryType, true)
                    )
                    ),
                    true
        ),
        true
        ),
        StructField(
        "alt_udl_info",
        ArrayType(
            StructType(
            List(
                StructField("udl_nm",            StringType, true),
                StructField("udl_desc",          StringType, true),
                StructField("products",          BinaryType, true),
                StructField("constituent_group", StringType, true),
                StructField("constituent_reqd",  StringType, true),
                StructField("constituent_rank",  StringType, true)
            )
            ),
            true
        ),
        true
        ),
        StructField("shared_qual",       StringType, true),
        StructField("override_tac_name", StringType, true),
        StructField("override_tar_name", StringType, true),
        StructField("newline",           StringType, true)
    )
    )
)

val origColumns = in0.columns.map(col)
val out0 = in0
    .groupBy("tal_assoc_id")
    .agg(
    collect_list(
        struct(
        (origColumns :+ lookup("Expanded_UDL", col("target_udl_name")).alias("trgt_prdcts_lkp")
            :+ lookup("Expanded_UDL", col("alt_udl_name")).alias("alt_prdcts_lkp"): _*) //15,16
        ).alias("inputRows")
    ),
    transform(lookup_row("LKP_CLIN_INDCN", last(col("tal_assoc_name"))),
                xx ⇒ coalesce(xx.getField("clinical_indn_desc"), lit("N/A"))
    ).alias("clinical_indn_desc")
    )
    .select((process_udf(col("inputRows"), col("clinical_indn_desc"))).alias("output"))
    .select(col("output.*"))
